using System.Collections;using System.Collections.Generic;using UnityEngine;public class Game2CarManager : MonoBehaviour {    public static Game2CarManager game2CarManager { get; private set; }    public static float widthOneRoad;    public static Color colorRed;    public static Color colorBlue;    public static bool pause;    public static bool die;    public static int score;    public float moveSpeed = 200;    public float maxSpeed = 500;    public float speedUp = 100;    public float lerpTime = 1;    public int numberItem = 20;    public float timeDown { get; private set; }    public int number { get; private set; }    private int itemeated;    void Awake()    {        if(game2CarManager == null)        {            game2CarManager = this;            widthOneRoad = (Camera.main.orthographicSize * Camera.main.aspect) / 2f;            colorRed = new Color(255, 0, 0, 255);            colorBlue = new Color(0, 0, 255, 255);            pause = false;            die = false;            timeDown = lerpTime / 4f;            number = 0;            itemeated = 0;            if (moveSpeed < 0)                moveSpeed = 0;            if (moveSpeed > maxSpeed)                moveSpeed = maxSpeed;        }    }    public void Score()    {        itemeated++;        if(itemeated == numberItem * (number + 1))        {            number++;            Game2CarManager.game2CarManager.lerpTime -= Game2CarManager.game2CarManager.timeDown;            if (lerpTime < timeDown)                lerpTime = timeDown;        }    }}